import React, { Component } from 'react';
import { HashRouter as Router, Route, Redirect, Switch } from 'react-router-dom';
import AddStudent from './AddStudent';
import StudentOverview from './StudentOverview';
import './App.css';
import './index.css';

class App extends Component {

  render() {

    return (
      <div className="App">
      <a className='btn btn-warning btn-large linkmargin' href='#/add-student' target='_self'>Schüler hinzufügen</a>
      <a className='btn btn-warning btn-large linkmargin' href='#/student-overview' target='_self'>Alle Schüler</a>
      <a className='btn btn-warning btn-large linkmargin' href='http://localhost:8080/schule/export' target='_self'>Download CSV-Export</a>

        <Router>
          <Switch>
            <Route exact path={"/add-student"} component={AddStudent} />
            <Route exact path="/student-overview" component={StudentOverview} />

            <Redirect to="/student-overview" component={StudentOverview} /> 
          </Switch>
        </Router>
      </div>
    );
  }
}

export default App;
