import React, { Component } from 'react';
import { HashRouter as Router, Route, Redirect, Switch } from 'react-router-dom';
import AddStudent from './AddStudent';
import StudentOverview from './StudentOverview';
import './App.css';
import './index.css';

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      isAuthenticated: false,
      user: {
        username: '',
        password: ''
      }
    }
  }

  handleSubmit = (event) => {
    let data = JSON.stringify(this.state.user)
    fetch(`http://localhost:8080/schule/login`, {
      method: 'POST',
      headers: {
          "Content-Type": "application/json",
      },
      body: data
  })
  .then(res => {
    if(res.status===200) {
      let user = {}
      this.setState({ isAuthenticated: true, user: user});
    }
  })
}

  handleChange = (event) => {
    let user = this.state.user;
    user[event.target.name] = event.target.value;
    this.setState({
        user: user 
    })
}

  render() {

    return (

      <div className="App">
        <a className='btn btn-warning btn-large linkmargin' href='#/add-student' target='_self'>Schüler hinzufügen</a>
        <a className='btn btn-warning btn-large linkmargin' href='#/student-overview' target='_self'>Alle Schüler</a>
        <a className='btn btn-info btn-large linkmargin' href='http://localhost:8080/schule/export' target='_self'>Download CSV-Export</a>

        <div className="login">

            Bitte einloggen
            <br/>
            <input required type='username' name='username' value={this.state.username} placeholder='Benutzername' onChange={(event) => this.handleChange(event)}/>
            <input required type='password' name='password' value={this.state.password} placeholder='Passwort' onChange={(event) => this.handleChange(event)}/>
            <br/>
            <br/>
            <button type='submit' onClick={this.handleSubmit}>Login</button>

        </div>

          <Router>
            <Switch>
              <Route exact path="/add-student" component={AddStudent} />
              <Route exact path="/student-overview" render={()=> <StudentOverview isAuthenticated={this.state.isAuthenticated} />} />

              <Redirect to="/student-overview" render={()=> <StudentOverview isAuthenticated={this.state.isAuthenticated} />} />
            </Switch>
          </Router>
      </div>
    );
  }
}

export default App;
