import React, { Component } from 'react';
import { HashRouter as Router, Route, Redirect, Switch } from 'react-router-dom';
import AddStudent from './AddStudent';
import StudentOverview from './StudentOverview';
import Login from './Login';
import PrivateRoute from './PrivateRoute';
import './App.css';
import './index.css';

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      isAuthenticated: false,
      user: {
        username: '',
        password: ''
    }
    }
  }

  handleChange = (event) => {
    let user = this.state.user;
    user[event.target.name] = event.target.value;
    this.setState({
        user: user 
    })
}

  render() {

    return (
      <div className="App">
      <a className='btn btn-warning btn-large linkmargin' href='#/add-student' target='_self'>Schüler hinzufügen</a>
      <a className='btn btn-warning btn-large linkmargin' href='#/student-overview' target='_self'>Alle Schüler</a>
      <a className='btn btn-info btn-large linkmargin' href='http://localhost:8080/schule/export' target='_self'>Download CSV-Export</a>

      <div className="Login">
      <form>
          Bitte einloggen
          <br/>

          <input required type='username' name='username' value={this.state.username} placeholder='Benutzername' onChange={(event) => this.handleChange(event)}/>
          <input required type='password' name='password' value={this.state.password} placeholder='Passwort' onChange={(event) => this.handleChange(event)}/>
        <br/>
        <br/>
          <input type='submit' value='Login'/>
      </form>

      </div>

        <Router>
          <Switch>
            <PrivateRoute exact path={"/add-student"} component={AddStudent} />
            <PrivateRoute exact path="/student-overview" component={StudentOverview} />
            <Route exact path="/login" component={App} />

            <Redirect to="/login" component={App} /> 
          </Switch>
        </Router>
      </div>
    );
  }
}

export default App;
