import { HashRouter as Router, Route, Redirect, Switch } from 'react-router-dom';
import AddStudent from './AddStudent';
import StudentOverview from './StudentOverview';
import './App.css';
import './index.css';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: ''
        };
    }


handleChange = (event) => {
    let value = event.target.value;
    this.setState({
        username: value 
    })
}

  render() {

    return (
      <div className="Login">
      <form>
          Bitte einloggen
          <input type='username' name='username' value={this.state.username} placerholder='Benutzername' onChange={(event) => this.handleChange(event)}/>
      </form>

      </div>
    );
  }
}

export default Login;
