import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import ReactTable from 'react-table';
import Modal from './Modal.js';
import "react-table/react-table.css";


class StudentOverview extends Component {
    state = {
        students: [],
        isOpen: false,
        studentSelected: {}
      };

      async componentDidMount() {
        fetch(`http://localhost:8080/schule`)
        .then(result => result.json())
        .then(studentList => {
            console.log(studentList);
            this.setState({
                students: studentList
            });
        });
    }

    toggleModal = (props) => {
        this.setState({
          isOpen: !this.state.isOpen,
          studentSelected: props
        });
      };

      approveDelete = (studentid) => {
        fetch(`http://localhost:8080/schule/${studentid}`, {
            method: 'delete'
        }).then(() => {
            this.setState({
                studentSelected: {},            
        });
        this.toggleModal();
        })
      }

    render() {

        function getLinkObject(props)
        {
            const newLink = {
                pathname: "/add-student",
                studentId: props.original.id
            }
            return newLink;
        }

        const columns = [{
            Header: 'Nachname',
            accessor: 'lastName',
            filterable: true
          }, {
            Header: 'Vorname',
            accessor: 'firstName',
            filterable: true
          }, {
            Header: 'Email',
            accessor: 'email',
            filterable: true
          }, {
            Header: 'Klasse',
            accessor: 'classId',
            filterable: true
          }, {
            Header: 'Unternehmen',
            accessor: 'company.company',
            filterable: true
          }, {
              Header: '',
              Cell: props => (
                    <div>
                        <Link to={getLinkObject(props)}><i className='fa fa-pencil fa-lg editStudentIcon'></i></Link>
                    </div>
              ),
              width: 50,
              resizable: false
          }, {
            Header: '',
            Cell: props => (
                  <div>
                      <i onClick={() => this.toggleModal(props.original)} className='fa fa-trash fa-lg deleteStudentIcon'></i>
                  </div>
            ),
            width: 50,
            resizable: false
        }
          ]

        return (
            <div>
                <div className='tableWrapper'>
                    <ReactTable
                        className={'-striped -highlight'}
                        data={this.state.students}
                        columns={columns}
                        defaultPageSize={20}
                    />
                </div>
                    <Modal student={this.state.studentSelected} show={this.state.isOpen} onApprove={this.approveDelete} onClose={this.toggleModal}>
                    </Modal>
            </div>
        )
    }
}

export default StudentOverview;
